import { Col, Row, Image } from 'antd';
import React from 'react';
import useTranslation from '../../components/translation/useTranslation';



function GeneralData({ data }) {

  const {trans} = useTranslation();

  const renderTestResult = (id) => {
    switch (id) {
      case -1:
        return trans("forbidden")
      case 0:
        return "გაუმართავი"
      case 1:
        return "გამართული"
      default:
        break;
    }
  }

  const generalInfo = [
    {
      name: trans("registration_number"),
      value: data?.vehicle?.plateNumber.toUpperCase()
    },
    {
      name: trans("mark"),
      value: data?.vehicle?.mark,
    },
    {
      name: trans("model"),
      value: data?.vehicle?.model,
    },
    {
      name: trans("vin_code"),
      value: data?.vehicle?.vinCode,
    },
    {
      name: trans("vehicle_manufacture_date"),
      value: data?.vehicle?.initialRegistrationDate,
    },
    {
      name: trans("engine_type"),
      value: data?.vehicle?.enginePower,
    },
    {
      name: trans("engine_vol"),
      value: data?.vehicle?.engineVolume,
    },
    {
      name: "ფერი",
      value: data?.vehicle?.color,
    },
 
  ]

  const condition = [
    {
      name: "ტესტირების შედეგი",
      value: renderTestResult(data?.testResult),
    },
    {
      name: "ტესტირების თარიღი",
      value: data?.inspectionDate,
    },
    {
      name: "ტესტირების ტიპი:",
      value: data?.testType === 1 ? "პირველადი" : "განმეორებითი",
    },
    {
      name: "პირველადი პტი თარიღი",
      value: data?.testType === 1 ? data?.inspectionDate : data?.InspectionStartDate,
    },
    {
      name: "მოქმედების ვადა:",
      value: data?.testResult === 1 ? data?.nextInspectionDate : "",

    },
    {
      name: "საკონტაქტო",
      value: data?.owner?.mobileNumber,
    },
    {
      name: "საჭის მდებარეობა",
      value: data?.vehicle?.wheelPosition === 1 ? "მარცხენა" : "მარჯვენა",
    },
    {
      name: "კატეგორია",
      value: data?.vehicle?.bodyType,
    }
  ]

  const photos = [
    "https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png",
    "https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png",
    "https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png",
  ]

  return <div className='general-data'>
    <Row gutter={[32, 32]}>
      <Col xs={24} sm={12} >
        <div className="general">
          {generalInfo?.map((item, index) => {
            return (
              <div key={index} className='data-item'>
                <div>{item.name}</div>
                <div>{item.value}</div>
              </div>
            )
          })}
        </div>
      </Col>
      <Col xs={24} sm={12} >
        <div className="test-result">
          {condition?.map((item, index) => {
            return (
              <div key={index} className='data-item'>
                <div>{item.name}</div>
                <div>{item.value}</div>
              </div>
            )
          })}
        </div>
      </Col>
      <Col xs={24} >
        <div className="photos">
          {photos?.map((photo, index) => {
            return (
              <div className="photo-item" key={index}>
                <Image
                  width={200}
                  src={photo}
                  fallback="data:image/png;base64,"
                />
              </div>
            )
          })}
        </div>
      </Col>
    </Row>
  </div>;
}

export default GeneralData;
