import React from 'react';
import { Col, Row, Image } from 'antd';



const History = ({ data }) => {
  // function History() {

  const photoArr = [
    "https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png",
    "https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png",
    "https://zos.alipayobjects.com/rmsportal/jkjgkEfvpUPVyRjUImniVslZfWPnJuuZ.png",
  ]

  return <div className='history'>
    {data.length < 1 ? <div className='not-found-wrapper'> <h3 className='data-error'>მონაცემები ვერ მოიძებნა</h3></div> : data?.map((car, index) => {
      return (
        <Row key={car?.id} justify={"center"} className={`${index % 2 === 0 && "odd"}`}>
          <Col xs={24} md={12} className='date-wrapper'>
            <div className="date">{car?.inspectionDate}</div>
            <div className="line"><div className={`${index === data.length-1 && index % 2 !== 0 && "arrow-end"}`}/></div>
            <div className={`second-line`}><div className={`${index === data.length-1 && index % 2 === 0 && "arrow-end"}`}/></div>
          </Col>

          <Col xs={24} md={12} className='data-wrapper'>
            <div className="photos">
              {photoArr?.map((photo, photoIndex) => {
                return (
                  <Image
                    key={photoIndex}
                    width={200}
                    src={photo}
                    fallback="data:image/png;base64,"
                  />
                )
              })}
            </div>
            <Row >
              <Col xs={12} className='data-item'>
                <div>სარეგისტრაციო ნომერი</div>
                <div>{car?.plateNumber.toUpperCase()}</div>
              </Col>
              <Col xs={12} className='data-item'>
                <div>მარკა</div>
                <div>{car?.mark}</div>
              </Col>
              <Col xs={12} className='data-item'>
                <div>მოდელი</div>
                <div>{car?.model}</div>
              </Col>
              <Col xs={12} className='data-item'>
                <div>VIN კოდი</div>
                <div>{car?.vinCode}</div>
              </Col>
              <Col xs={12} className='data-item'>
                <div>კატეგორია</div>
                <div>{car?.category}</div>
              </Col>
              <Col xs={12} className='data-item'>
                <div>პტი ტიპი</div>
                <div>{car?.testType}</div>
              </Col>
              <Col xs={12} className='data-item'>
                <div>შედეგი</div>
                <div>{car?.testResult}</div>
              </Col>
              <Col xs={12} className='data-item'>
                <div>ტესტირების თარიღი</div>
                <div>{car?.inspectionDate}</div>
              </Col>
              <Col xs={12} className='data-item'>
                <div>მოქმედების ვადა</div>
                <div>{car?.nextInspectionDate}</div>
              </Col>
              <Col xs={12} className=' details'>
                <div>დეტალურად ნახვა</div>
              </Col>
            </Row>
          </Col>
        </Row>

      )
    })}
  </div>;
}

export default History;
